import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import SubAgentForm from './SubAgentForm';
import EditIcon from '@mui/icons-material/EditOutlined';
import DeleteIcon from '@mui/icons-material/DeleteOutlined';
import { Modal } from 'react-bootstrap';
import '../styles/SubAgentList.css';

const SubAgentList = () => {
  const { masterAgentId } = useParams();
  const [subAgents, setSubAgents] = useState([]);
  const [masterAgent, setMasterAgent] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [editSubAgent, setEditSubAgent] = useState(null);

  const loadSubAgents = useCallback(async () => {
    try {
      const result = await axios.get(`http://localhost:5001/api/sub-agents/${masterAgentId}`);
      setSubAgents(result.data);
    } catch (error) {
      console.error('Error loading sub agents:', error);
    }
  }, [masterAgentId]);

  const loadMasterAgent = useCallback(async () => {
    try {
      const result = await axios.get(`http://localhost:5001/api/master-agents/${masterAgentId}`);
      setMasterAgent(result.data);
    } catch (error) {
      console.error('Error loading master agent:', error);
    }
  }, [masterAgentId]);

  const handleShowForm = () => {
    setEditSubAgent(null); // Reset the form for adding new sub-agent
    setShowForm(true);
  };

  const handleCloseForm = () => {
    setShowForm(false);
    loadSubAgents();
  };

  const handleDeleteAgent = async (id) => {
    try {
      await axios.delete(`http://localhost:5001/api/sub-agents/${id}`);
      loadSubAgents();
    } catch (error) {
      console.error('Error deleting sub agent:', error);
    }
  };

  const handleEditAgent = (agent) => {
    setEditSubAgent(agent);
    setShowForm(true);
  };

  useEffect(() => {
    loadSubAgents();
    loadMasterAgent();
  }, [loadSubAgents, loadMasterAgent]);

  return (
    <div className="container mt-3">
      {masterAgent && (
        <>
          <h2>Sub Agents of {masterAgent.Name}</h2>
          <p>{masterAgent.Details}</p>
          <SubAgentForm masterAgentId={masterAgentId} onAdd={loadSubAgents} editSubAgent={editSubAgent} />
        </>
      )}
      <div className="instructions">
        <h3>Instructions:</h3>
        <p>Add a new sub-agent to this master agent by filling the form above. You can also edit or delete existing sub-agents.</p>
      </div>
      <div className="row mt-3">
        {subAgents.map(agent => (
          <div key={agent.SubAgentId} className="col-md-4 mb-3">
            <div className="card">
              <div className="card-body">
                <h5 className="card-title">{agent.Name}</h5>
                <p className="card-text">{agent.Details}</p>
                <div className="action-buttons">
                  <EditIcon className="icon edit-icon" onClick={() => handleEditAgent(agent)} />
                  <DeleteIcon className="icon delete-icon" onClick={() => handleDeleteAgent(agent.SubAgentId)} />
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Modal for adding/editing Sub Agent */}
      <Modal show={showForm} onHide={handleCloseForm}>
        <Modal.Header closeButton>
          <Modal.Title>{editSubAgent ? 'Edit Sub Agent' : 'Add Sub Agent'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <SubAgentForm masterAgentId={masterAgentId} onAdd={handleCloseForm} editSubAgent={editSubAgent} />
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default SubAgentList;
