import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MasterAgentForm = ({ onAdd, agent, formType }) => {
  const [name, setName] = useState('');
  const [category, setCategory] = useState('');
  const [avatar, setAvatar] = useState('');

  useEffect(() => {
    if (agent) {
      setName(agent.Name);
      setCategory(agent.Category);
      setAvatar(agent.Avatar);
    }
  }, [agent]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const payload = { name, category, avatar };
    try {
      if (formType === 'Edit') {
        await axios.put(`http://localhost:5001/api/master-agents/${agent.MasterAgentId}`, payload);
      } else {
        await axios.post('http://localhost:5001/api/master-agents', payload);
      }
      onAdd();
    } catch (error) {
      console.error('Error adding/updating master agent:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="form-group">
        <label htmlFor="name">Name</label>
        <input
          type="text"
          id="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
          className="form-control"
        />
      </div>
      <div className="form-group">
        <label htmlFor="category">Category</label>
        <input
          type="text"
          id="category"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          required
          className="form-control"
        />
      </div>
      <div className="form-group">
        <label htmlFor="avatar">Avatar URL</label>
        <input
          type="text"
          id="avatar"
          value={avatar}
          onChange={(e) => setAvatar(e.target.value)}
          className="form-control"
        />
      </div>
      <button type="submit" className="btn btn-primary">
        {formType === 'Edit' ? 'Update' : 'Add'} Master Agent
      </button>
    </form>
  );
};

export default MasterAgentForm;
