import React, { useState, useEffect } from 'react';
import { Form, Button } from 'react-bootstrap';
import axios from 'axios';

const SubAgentForm = ({ masterAgentId, onAdd, editSubAgent }) => {
  const [name, setName] = useState('');
  const [details, setDetails] = useState('');
  const [avatar, setAvatar] = useState('');

  useEffect(() => {
    if (editSubAgent) {
      setName(editSubAgent.Name);
      setDetails(editSubAgent.Details);
      setAvatar(editSubAgent.Avatar || '');
    } else {
      setName('');
      setDetails('');
      setAvatar('');
    }
  }, [editSubAgent]);

  const handleAvatarChange = (e) => {
    const file = e.target.files[0];
    if (file && file.size < 400 * 1024) { // Check file size (< 400 KB)
      const reader = new FileReader();
      reader.onloadend = () => {
        setAvatar(reader.result);
      };
      reader.readAsDataURL(file); // Encode image as Base64
    } else {
      alert('File is too large. Please select a file smaller than 400 KB.');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (!editSubAgent) {
        // Add Sub Agent
        await axios.post('http://localhost:5001/api/sub-agents', {
          masterAgentId,
          name,
          details,
          avatar,
        });
      } else {
        // Edit Sub Agent
        await axios.put(`http://localhost:5001/api/sub-agents/${editSubAgent.SubAgentId}`, {
          name,
          details,
          avatar,
        });
      }
      onAdd();
    } catch (error) {
      console.error(`Error ${editSubAgent ? 'editing' : 'adding'} sub agent:`, error);
    }
  };

  return (
    <Form onSubmit={handleSubmit}>
      <Form.Group controlId="formName">
        <Form.Label>Name</Form.Label>
        <Form.Control
          type="text"
          placeholder="Enter name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </Form.Group>

      <Form.Group controlId="formDetails" className="mt-3">
        <Form.Label>Details</Form.Label>
        <Form.Control
          type="text"
          placeholder="Enter details"
          value={details}
          onChange={(e) => setDetails(e.target.value)}
        />
      </Form.Group>

      <Form.Group controlId="formAvatar" className="mt-3">
        <Form.Label>Avatar</Form.Label>
        <Form.Control
          type="file"
          accept="image/*"
          onChange={handleAvatarChange}
        />
      </Form.Group>

      <Button variant="primary" type="submit" className="mt-3">
        {editSubAgent ? 'Update Sub Agent' : 'Add Sub Agent'}
      </Button>
    </Form>
  );
};

export default SubAgentForm;
