import React, { useState, useEffect } from 'react';
import { Form, Button, Modal } from 'react-bootstrap';
import axios from 'axios';

const SubAgentForm = ({ masterAgentId, onAdd, agent, formType }) => {
  const [name, setName] = useState('');
  const [details, setDetails] = useState('');
  const [avatar, setAvatar] = useState('');

  useEffect(() => {
    if (formType === 'Edit' && agent) {
      setName(agent.Name);
      setDetails(agent.Details);
      setAvatar(agent.Avatar || '');
    }
  }, [formType, agent]);

  const handleAvatarChange = (e) => {
    const file = e.target.files[0];
    if (file && file.size < 400 * 1024) { // Check file size (< 400 KB)
      const reader = new FileReader();
      reader.onloadend = () => {
        setAvatar(reader.result);
      };
      reader.readAsDataURL(file); // Encode image as Base64
    } else {
      alert('File is too large. Please select a file smaller than 400 KB.');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (formType === 'Add') {
        await axios.post('http://localhost:5001/api/sub-agents', { masterAgentId, name, details, avatar });
      } else if (formType === 'Edit' && agent) {
        await axios.put(`http://localhost:5001/api/sub-agents/${agent.SubAgentId}`, { name, details, avatar });
      }
      onAdd();
    } catch (error) {
      console.error(`Error ${formType === 'Add' ? 'adding' : 'editing'} sub agent:`, error);
    }
  };

  return (
    <>
      <Button variant="primary" onClick={() => setShow(true)}>
        {formType === 'Add' ? 'Add Sub Agent' : 'Update Sub Agent'}
      </Button>

      <Modal show={show} onHide={() => setShow(false)}>
        <Modal.Header closeButton>
          <Modal.Title>{formType === 'Add' ? 'Add Sub Agent' : 'Update Sub Agent'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleSubmit}>
            <Form.Group controlId="formName">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter name"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </Form.Group>

            <Form.Group controlId="formDetails" className="mt-3">
              <Form.Label>Details</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter details"
                value={details}
                onChange={(e) => setDetails(e.target.value)}
              />
            </Form.Group>

            <Form.Group controlId="formAvatar" className="mt-3">
              <Form.Label>Avatar</Form.Label>
              <Form.Control
                type="file"
                accept="image/*"
                onChange={handleAvatarChange}
              />
            </Form.Group>

            <Button variant="primary" type="submit" className="mt-3">
              {formType === 'Add' ? 'Add Sub Agent' : 'Update Sub Agent'}
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    </>
  );
};

export default SubAgentForm;
