import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Modal } from 'react-bootstrap';
import '../styles/SubAgentForm.css';

const SubAgentForm = ({ masterAgentId, onAdd, editSubAgent }) => {
  const [show, setShow] = useState(false);
  const [name, setName] = useState('');
  const [details, setDetails] = useState('');

  // Effect to populate form fields if editing an existing sub-agent
  useEffect(() => {
    if (editSubAgent) {
      setName(editSubAgent.Name);
      setDetails(editSubAgent.Details);
      setShow(true);
    }
  }, [editSubAgent]);

  const handleClose = () => {
    setShow(false);
    setName('');
    setDetails('');
  };

  const handleShow = () => setShow(true);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Check if we are adding a new sub-agent or updating an existing one
      if (editSubAgent) {
        await axios.put(`http://localhost:5001/api/sub-agents/${editSubAgent.SubAgentId}`, {
          masterAgentId,
          name,
          details,
        });
      } else {
        await axios.post('http://localhost:5001/api/sub-agents', {
          masterAgentId,
          name,
          details,
        });
      }
      onAdd();
      handleClose();
    } catch (error) {
      console.error(`Error ${editSubAgent ? 'editing' : 'adding'} sub agent:`, error);
    }
  };

  return (
    <>
      {!editSubAgent && (
        <Button variant="primary" onClick={handleShow}>
          Add Sub Agent
        </Button>
      )}

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>{editSubAgent ? 'Edit Sub Agent' : 'Add Sub Agent'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="name">Name</label>
              <input
                type="text"
                className="form-control"
                id="name"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </div>
            <div className="form-group">
              <label htmlFor="details">Details</label>
              <textarea
                className="form-control"
                id="details"
                value={details}
                onChange={(e) => setDetails(e.target.value)}
              ></textarea>
            </div>
            <Button variant="primary" type="submit">
              {editSubAgent ? 'Update Sub Agent' : 'Save Changes'}
            </Button>
          </form>
        </Modal.Body>
      </Modal>
    </>
  );
};

export default SubAgentForm;
