import React, { useState, useEffect } from 'react';
import { Form, Button } from 'react-bootstrap';
import axios from 'axios';

const MasterAgentForm = ({ onAdd, agent, formType }) => {
  const [name, setName] = useState('');
  const [details, setDetails] = useState('');

  useEffect(() => {
    if (formType === 'Edit' && agent) {
      setName(agent.Name);
      setDetails(agent.Details);
    }
  }, [formType, agent]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (formType === 'Add') {
        await axios.post('http://localhost:5001/api/master-agents', { name, details });
      } else if (formType === 'Edit' && agent) {
        await axios.put(`http://localhost:5001/api/master-agents/${agent.MasterAgentId}`, { name, details });
      }
      onAdd();
    } catch (error) {
      console.error(`Error ${formType === 'Add' ? 'adding' : 'editing'} master agent:`, error);
    }
  };

  return (
    <Form onSubmit={handleSubmit}>
      <Form.Group controlId="formName">
        <Form.Label>Name</Form.Label>
        <Form.Control
          type="text"
          placeholder="Enter name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </Form.Group>

      <Form.Group controlId="formDetails" className="mt-3">
        <Form.Label>Details</Form.Label>
        <Form.Control
          type="text"
          placeholder="Enter details"
          value={details}
          onChange={(e) => setDetails(e.target.value)}
        />
      </Form.Group>

      <Button variant="primary" type="submit" className="mt-3">
        {formType === 'Add' ? 'Add Master Agent' : 'Update Master Agent'}
      </Button>
    </Form>
  );
};

export default MasterAgentForm;
